INSERT INTO `post` (`post_no`, `user_id`, `board_id`, `pinned`, `user_name`, `title`, `content_sanitized`, `content_original`, `comments`, `date`) VALUES
(94, '2', 'announcement', 0, 'Byunghoon Yoon', 'Invitation to the University Fair! (Nov 5th)', '<span class="Apple-style-span"></span><div class="MsoNormal" style="font-family:Verdana, Tahoma, ''돋움'', Dotum;font-size:10pt;color:rgb(59,61,60);"><i><span lang="en-us" xml:lang="en-us" style="font-size:10pt;">Trouble finding the right university?</span></i></div><div class="MsoNormal" style="font-family:Verdana, Tahoma, ''돋움'', Dotum;font-size:10pt;color:rgb(59,61,60);"><i><span lang="en-us" xml:lang="en-us" style="font-size:10pt;">Want to find your way around?</span></i></div><div class="MsoNormal" style="font-family:Verdana, Tahoma, ''돋움'', Dotum;font-size:10pt;color:rgb(59,61,60);"><i><span lang="en-us" xml:lang="en-us" style="font-size:10pt;"><br /></span></i></div><div class="MsoNormal" style="font-family:Verdana, Tahoma, ''돋움'', Dotum;font-size:10pt;color:rgb(59,61,60);"><span lang="en-us" xml:lang="en-us" style="font-size:10pt;">The University Fair </span><b><span lang="en-us" xml:lang="en-us" style="font-size:12pt;">"Gate way to the future"</span></b><span lang="en-us" xml:lang="en-us" style="font-size:12pt;"> </span><span lang="en-us" xml:lang="en-us" style="font-size:10pt;">will be the greatest opportunity </span><span lang="en-us" xml:lang="en-us" style="font-size:10pt;">for high school students to gain information regarding universities from head-to-toe!</span></div><div class="MsoNormal" style="font-family:Verdana, Tahoma, ''돋움'', Dotum;font-size:10pt;color:rgb(59,61,60);"><b><span lang="en-us" xml:lang="en-us" style="font-size:10pt;"><br /></span></b></div><div class="MsoNormal" style="font-family:Verdana, Tahoma, ''돋움'', Dotum;font-size:10pt;color:rgb(59,61,60);"><b><span lang="en-us" xml:lang="en-us" style="font-size:10pt;">When</span></b><span lang="en-us" xml:lang="en-us" style="font-size:10pt;">: Saturday November 5th, 10am - 4pm</span></div><div class="MsoNormal" style="font-family:Verdana, Tahoma, ''돋움'', Dotum;font-size:10pt;color:rgb(59,61,60);"><b><span lang="en-us" xml:lang="en-us" style="font-size:10pt;">Where</span></b><span lang="en-us" xml:lang="en-us" style="font-size:10pt;">: EarlHaig Secondary School</span></div><div class="MsoNormal" style="font-family:Verdana, Tahoma, ''돋움'', Dotum;font-size:10pt;color:rgb(59,61,60);text-indent:40pt;"><span lang="en-us" xml:lang="en-us" style="font-size:10pt;">(100 princess ave., 5 min walk from North York Centre station)</span></div><div class="MsoNormal" style="font-family:Verdana, Tahoma, ''돋움'', Dotum;font-size:10pt;color:rgb(59,61,60);"><b><span lang="en-us" xml:lang="en-us" style="font-size:10pt;">Our participating universities are: McGill, University of Toronto, Waterloo, McMaster, Western,Queen''s, Ottawa, UOIT, McMaster, Laurier, York (schulich), Ryerson, OCAD, Seneca, George Brown..</span></b></div><div class="MsoNormal" style="font-family:Verdana, Tahoma, ''돋움'', Dotum;font-size:10pt;color:rgb(59,61,60);"><span lang="en-us" xml:lang="en-us" style="font-size:10pt;background-color:rgb(255,255,0);"><br /></span></div><div class="MsoNormal" style="font-family:Verdana, Tahoma, ''돋움'', Dotum;font-size:10pt;color:rgb(59,61,60);"><span lang="en-us" xml:lang="en-us" style="font-size:10pt;background-color:rgb(255,255,0);">PLUS!!</span></div><div class="MsoNormal" style="font-family:Verdana, Tahoma, ''돋움'', Dotum;font-size:10pt;color:rgb(59,61,60);"><span lang="en-us" xml:lang="en-us" style="font-size:10pt;">we will have guest speaker </span><b><span lang="en-us" xml:lang="en-us" style="font-size:12pt;">Dr. Ju</span></b><span lang="en-us" xml:lang="en-us" style="font-size:10pt;">, <b>a neuroscience professor from the University of Toronto</b>, who will be giving us a special presentation on how to excel in University!</span></div><div class="MsoNormal" style="font-family:Verdana, Tahoma, ''돋움'', Dotum;font-size:10pt;color:rgb(59,61,60);"><span lang="en-us" xml:lang="en-us" style="font-size:10pt;">AND <b>many mentors</b> (university students froma variety of facult